import { useState } from "react";
import { Menu, X } from "lucide-react";
import { Button } from "@/components/ui/button";

export default function Navigation() {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const navItems = [
    { href: "#home", label: "Beranda" },
    { href: "#properties", label: "Properti" },
    { href: "#jeep", label: "Sewa Jeep" },
    { href: "#about", label: "Tentang" },
    { href: "#contact", label: "Kontak" },
  ];

  const handleNavClick = (href: string) => {
    setIsMenuOpen(false);
    const element = document.querySelector(href);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth', block: 'start' });
    }
  };

  return (
    <nav className="bg-white shadow-lg sticky top-0 z-40" data-testid="navigation">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-16">
          <div className="flex items-center">
            <div className="flex-shrink-0">
              <h1 className="text-xl font-bold text-primary-700" data-testid="text-logo">
                BOS VILLA TAWANGMANGU
              </h1>
            </div>
          </div>

          {/* Desktop Navigation */}
          <div className="hidden md:block">
            <div className="ml-10 flex items-baseline space-x-4">
              {navItems.map((item, index) => (
                <button
                  key={item.href}
                  onClick={() => handleNavClick(item.href)}
                  className={`px-3 py-2 rounded-md text-sm font-medium transition-colors ${
                    index === 0 
                      ? "text-gray-900 hover:text-primary-600" 
                      : "text-gray-700 hover:text-primary-600"
                  }`}
                  data-testid={`link-${item.label.toLowerCase()}`}
                >
                  {item.label}
                </button>
              ))}
            </div>
          </div>

          {/* Mobile menu button */}
          <div className="md:hidden">
            <Button
              variant="ghost"
              size="sm"
              onClick={() => setIsMenuOpen(!isMenuOpen)}
              className="relative p-2 hover:bg-gray-100 transition-colors duration-200"
              data-testid="button-mobile-menu"
            >
              <div className="relative w-6 h-6">
                <Menu className={`h-6 w-6 absolute transition-all duration-300 ${isMenuOpen ? 'opacity-0 rotate-90' : 'opacity-100 rotate-0'}`} />
                <X className={`h-6 w-6 absolute transition-all duration-300 ${isMenuOpen ? 'opacity-100 rotate-0' : 'opacity-0 -rotate-90'}`} />
              </div>
            </Button>
          </div>
        </div>
      </div>

      {/* Mobile menu */}
      <div className={`md:hidden overflow-hidden transition-all duration-300 ease-in-out ${
        isMenuOpen ? 'max-h-96 opacity-100' : 'max-h-0 opacity-0'
      }`} data-testid="mobile-menu">
        <div className="bg-white border-t border-gray-200 shadow-lg backdrop-blur-sm">
          <div className="px-4 py-4 space-y-2">
            {navItems.map((item, index) => (
              <button
                key={item.href}
                onClick={() => handleNavClick(item.href)}
                className={`text-gray-700 hover:text-primary-600 hover:bg-primary-50 block px-4 py-3 rounded-xl text-base font-medium w-full text-left transition-all duration-200 transform hover:translate-x-2 hover:shadow-md ${
                  isMenuOpen ? 'animate-slide-in' : ''
                }`}
                style={{
                  animationDelay: `${index * 50}ms`
                }}
                data-testid={`link-mobile-${item.label.toLowerCase()}`}
              >
                <span className="flex items-center">
                  <span className="w-2 h-2 bg-primary-500 rounded-full mr-3 opacity-60"></span>
                  {item.label}
                </span>
              </button>
            ))}
          </div>

          {/* Menu footer with contact info */}
          <div className="px-4 py-3 bg-gradient-to-r from-primary-50 to-blue-50 border-t border-gray-100">
            <p className="text-sm text-gray-600 text-center font-medium">
              üí¨ Hubungi kami untuk info lebih lanjut
            </p>
            <div className="flex justify-center mt-2">
              <button
                onClick={() => {
                  setIsMenuOpen(false);
                  window.open('https://wa.me/6281226374041?text=Halo%2C%20saya%20tertarik%20dengan%20BOS%20VILLA%20TAWANGMANGU', '_blank');
                }}
                className="text-green-600 text-sm font-semibold hover:text-green-700 transition-colors duration-200"
              >
                WhatsApp: 0812-2637-4041
              </button>
            </div>
          </div>
        </div>
      </div>
    </nav>
  );
}

client/src/components/property-card.tsx.content:
import { MapPin, Users, Star } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import type { Property } from "@shared/schema";

interface PropertyCardProps {
  property: Property;
  onViewDetail: () => void;
}

export default function PropertyCard({ property, onViewDetail }: PropertyCardProps) {
  const minPrice = Math.min(...property.rates.map(rate => rate.price));
  const formattedPrice = new Intl.NumberFormat('id-ID', {
    style: 'currency',
    currency: 'IDR',
    minimumFractionDigits: 0,
    maximumFractionDigits: 0
  }).format(minPrice);

  const rating = property.rating ?? 4.5;
  const fullStars = Math.floor(rating);
  const hasHalfStar = rating % 1 !== 0;

  return (
    <div 
      className="bg-white rounded-xl shadow-lg overflow-hidden hover:shadow-xl transition-shadow duration-300"
      data-testid={`card-property-${property.id}`}
    >
      <div className="relative">
        <img 
          src={property.image} 
          alt={property.name} 
          className="w-full h-64 object-cover"
          data-testid={`img-property-${property.id}`}
        />
        <div className="absolute top-4 left-4">
          <Badge 
            className="bg-primary-600 text-white px-3 py-1 rounded-full text-sm font-medium capitalize"
            data-testid={`badge-type-${property.id}`}
          >
            {property.type}
          </Badge>
        </div>
        <div className="absolute top-4 right-4">
          <Badge 
            className="bg-white text-gray-800 px-3 py-1 rounded-full text-sm font-medium"
            data-testid={`badge-units-${property.id}`}
          >
            {property.units} unit tersedia
          </Badge>
        </div>
      </div>

      <div className="p-6">
        <h3 className="text-xl font-bold text-gray-900 mb-2" data-testid={`text-name-${property.id}`}>
          {property.name}
        </h3>

        <p className="text-gray-600 mb-3 flex items-center" data-testid={`text-location-${property.id}`}>
          <MapPin className="mr-2 text-primary-600 h-4 w-4" />
          {property.location}
        </p>

        <p className="text-gray-600 mb-3 flex items-center" data-testid={`text-capacity-${property.id}`}>
          <Users className="mr-2 text-primary-600 h-4 w-4" />
          {property.capacity}
        </p>

        {/* Rating */}
        <div className="flex items-center mb-3" data-testid={`rating-${property.id}`}>
          <div className="flex items-center mr-2">
            {[...Array(5)].map((_, index) => (
              <Star
                key={index}
                className={`h-4 w-4 ${
                  index < fullStars
                    ? 'text-yellow-400 fill-current'
                    : index === fullStars && hasHalfStar
                    ? 'text-yellow-400 fill-current'
                    : 'text-gray-300'
                }`}
              />
            ))}
          </div>
          <span className="text-sm text-gray-600 font-medium">{rating.toFixed(1)}</span>
        </div>

        <div className="mb-4">
          <div className="flex flex-wrap gap-1 mb-3">
            {property.facilities.slice(0, 3).map((facility, index) => (
              <Badge 
                key={index}
                variant="secondary"
                className="bg-gray-100 text-gray-700 px-2 py-1 rounded-md text-xs"
                data-testid={`badge-facility-${property.id}-${index}`}
              >
                {facility}
              </Badge>
            ))}
            {property.facilities.length > 3 && (
              <Badge 
                variant="secondary"
                className="bg-gray-100 text-gray-700 px-2 py-1 rounded-md text-xs"
                data-testid={`badge-more-facilities-${property.id}`}
              >
                +{property.facilities.length - 3} lainnya
              </Badge>
            )}
          </div>
        </div>

        <div className="flex items-center justify-between">
          <div>
            <span className="text-2xl font-bold text-primary-600" data-testid={`text-price-${property.id}`}>
              {formattedPrice}
            </span>
            <span className="text-gray-600 text-sm">/malam</span>
          </div>
          <Button 
            onClick={onViewDetail}
            className="bg-primary-600 hover:bg-primary-700 text-white px-6 py-2 rounded-lg font-medium transition-colors duration-200"
            data-testid={`button-detail-${property.id}`}
          >
            Lihat Detail
          </Button>
        </div>
      </div>
    </div>
  );
}

